svg.styles:
.timeline-line { stroke: #00f2fe; stroke-width: 6; stroke-linecap: round; }
.node-circle { fill: #000; stroke: #00f2fe; stroke-width: 4; cursor: pointer; transition: all 0.3s; r: 12; }
.node-circle:hover { fill: #00f2fe; stroke: #fff; r: 16; }
.year-label { font-family: 'Segoe UI', sans-serif; font-size: 18px; fill: #00f2fe; font-weight: bold; text-anchor: middle; }
.title-label { font-family: 'Segoe UI', sans-serif; font-size: 18px; fill: #00f2fe; font-weight: bold; text-anchor: middle; opacity: 0; }

/* Animations */
@keyframes drawLine { from { stroke-dashoffset: 800; } to { stroke-dashoffset: 0; } }
/* Sequential appearance of nodes */
@keyframes popIn { 
    0% { transform: scale(0); opacity: 0; } 
    70% { transform: scale(1.2); opacity: 1; }
    100% { transform: scale(1); opacity: 1; }
}

@keyframes slideUp { from { transform: translateY(20px); opacity: 0; } to { transform: translateY(0); opacity: 1; } }

.line-anim { stroke-dasharray: 800; stroke-dashoffset: 800; animation: drawLine 2s ease-out forwards; }

/* Delays for Nodes */
.node-1 { animation: popIn 0.5s ease-out forwards 0.5s; transform-origin: 100px 150px; }
.node-2 { animation: popIn 0.5s ease-out forwards 1.0s; transform-origin: 250px 150px; }
.node-3 { animation: popIn 0.5s ease-out forwards 1.5s; transform-origin: 400px 150px; }
.node-4 { animation: popIn 0.5s ease-out forwards 2.0s; transform-origin: 550px 150px; }
.node-5 { animation: popIn 0.5s ease-out forwards 2.5s; transform-origin: 700px 150px; }

/* Delays for Titles to Fade In */
.title-1 { animation: slideUp 0.5s forwards 0.7s; }
.title-2 { animation: slideUp 0.5s forwards 1.2s; }
.title-3 { animation: slideUp 0.5s forwards 1.7s; }
.title-4 { animation: slideUp 0.5s forwards 2.2s; }
.title-5 { animation: slideUp 0.5s forwards 2.7s; }
